import React from 'react';

const DEFAULT_AVATAR = 'https://via.placeholder.com/100/007bff/ffffff?text=User';

export default function UserProfileCard({
  name,
  email,
  avatarUrl = DEFAULT_AVATAR,
  bio,
  isOnline = false,
  followersCount,
  onFollow
}) {
  return (
    <div style={{
      border: '1px solid #e0e0e0',
      borderRadius: '12px',
      padding: '24px',
      maxWidth: '320px',
      backgroundColor: '#ffffff',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
      transition: 'transform 0.2s ease, box-shadow 0.2s ease',
      ':hover': {
        transform: 'translateY(-4px)',
        boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)'
      }
    }}>
      {/* Avatar Section */}
      <div style={{ position: 'relative', marginBottom: '16px' }}>
        <img 
          src={avatarUrl} 
          alt={`${name}'s avatar`}
          style={{
            width: '100px',
            height: '100px',
            borderRadius: '50%',
            objectFit: 'cover',
            border: '3px solid #f0f0f0'
          }}
        />

        {/* Online Status Indicator */}
        {isOnline && (
          <div style={{
            position: 'absolute',
            bottom: '10px',
            right: '10px',
            width: '20px',
            height: '20px',
            backgroundColor: '#4CAF50',
            borderRadius: '50%',
            border: '3px solid white'
          }} />
        )}
      </div>

      {/* User Info Section */}
      <div>
        <h2 style={{
          margin: '0 0 8px 0',
          fontSize: '24px',
          color: '#333'
        }}>
          {name}
        </h2>

        <p style={{
          margin: '0 0 12px 0',
          color: '#666',
          fontSize: '14px'
        }}>
          {email}
        </p>

        {/* Bio */}
        {bio && (
          <p style={{
            margin: '0 0 16px 0',
            color: '#555',
            lineHeight: '1.5',
            fontSize: '15px'
          }}>
            {bio}
          </p>
        )}

        {/* Followers Count */}
        {followersCount !== undefined && (
          <div style={{
            marginBottom: '16px',
            color: '#777',
            fontSize: '14px'
          }}>
            <strong>{followersCount.toLocaleString()}</strong> followers
          </div>
        )}

        {/* Follow Button */}
        {onFollow && (
          <button
            onClick={onFollow}
            style={{
              width: '100%',
              padding: '12px 24px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '6px',
              fontSize: '16px',
              fontWeight: '600',
              cursor: 'pointer',
              transition: 'background-color 0.2s ease',
              ':hover': {
                backgroundColor: '#0056b3'
              }
            }}
          >
            Follow
          </button>
        )}
      </div>
    </div>
  );
}

// Note: The CSS-in-JS hover styles would need to be implemented with a CSS-in-JS library
// For plain React, you might use className and CSS classes instead
