import React from 'react';
import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  useParams,
  useNavigate
} from 'react-router-dom';

// Mock user data
const users = [
  {
    id: 1,
    name: 'Alice Johnson',
    email: 'alice@example.com',
    bio: 'Software Developer with 5 years of experience in React and Node.js'
  },
  {
    id: 2,
    name: 'Bob Smith',
    email: 'bob@example.com',
    bio: 'Product Manager specializing in agile development and user-centered design'
  },
  {
    id: 3,
    name: 'Charlie Brown',
    email: 'charlie@example.com',
    bio: 'UX Designer passionate about creating intuitive and beautiful interfaces'
  }
];

// UserList Component
function UserList() {
  return (
    <div>
      <h1>User Directory</h1>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {users.map(user => (
          <li key={user.id} style={{ margin: '10px 0' }}>
            <Link 
              to={`/users/${user.id}`}
              style={{
                textDecoration: 'none',
                color: '#007bff',
                fontSize: '18px',
                display: 'block',
                padding: '10px',
                border: '1px solid #ddd',
                borderRadius: '5px'
              }}
            >
              {user.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

// UserProfile Component
function UserProfile() {
  const { userId } = useParams();
  const navigate = useNavigate();

  // Convert userId string to number
  const userIdNum = parseInt(userId);

  // Find user by ID
  const user = users.find(u => u.id === userIdNum);

  // Handle user not found
  if (!user) {
    return (
      <div>
        <h1>User Not Found</h1>
        <p>No user exists with ID: {userId}</p>
        <button onClick={() => navigate('/users')}>
          Back to Users
        </button>
      </div>
    );
  }

  return (
    <div>
      <button 
        onClick={() => navigate('/users')}
        style={{ marginBottom: '20px' }}
      >
        ‚Üê Back to Users
      </button>

      <div style={{
        padding: '20px',
        border: '1px solid #ddd',
        borderRadius: '8px',
        backgroundColor: '#f9f9f9'
      }}>
        <h1>{user.name}</h1>
        <p><strong>Email:</strong> {user.email}</p>
        <p><strong>Bio:</strong> {user.bio}</p>
        <p><strong>User ID:</strong> {user.id}</p>
      </div>
    </div>
  );
}

// Main App Component
export default function App() {
  return (
    <BrowserRouter>
      <nav style={{ padding: '10px', backgroundColor: '#f0f0f0' }}>
        <Link to="/" style={{ marginRight: '10px' }}>Home</Link>
        <Link to="/users">Users</Link>
      </nav>

      <div style={{ padding: '20px' }}>
        <Routes>
          <Route path="/" element={<h1>Welcome to User Management System</h1>} />
          <Route path="/users" element={<UserList />} />
          <Route path="/users/:userId" element={<UserProfile />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}
